import React,{Component} from "react"

/* Initialize state*/

class MemeGenerator extends Component {
    constructor(){
        super()
        this.state = {
            topText : "" ,
            bottomText : "" ,
            randomImg : "https://imgflip.com/s/meme/Doge.jpg",
            allMemeImgs : []
        }

        this.handleChange = this.handleChange.bind(this)
    }

    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes")
        .then(response => response.json())
        .then(response => {
            const {memes} = response.data
            console.log(memes[0])
            this.setState({allMemeImgs: memes})
        })
    }

    handleChange(event){
        const {name,value}  = event.target;
        this.setState({[name]:value})
        console.log(this.state.topText)
    }

    render(){
        return(
            <div className="row bg-light container-fluid d-flex justify-content-center">
                <div class="col-md-6 justify-content-center align-items-center d-flex">
                <form className="form meme-form" onSubmit={this.handleSubmit}>
                    <input
                        type="text"
                        value={this.state.topText}
                        name="topText"
                        placeholder="Top Text"
                        onChange={this.handleChange}
                        className="form-control form-control-lg mt-2 input-lg"
                    />
                    <br />
                    <input
                        type="text"
                        value={this.state.bottomText}
                        name="bottomText"
                        placeholder="Bottom Text"
                        onChange={this.handleChange}
                        className="form-control form-control-lg input-lg mt-2"
                    />
                    <br />
                    <button
                        className="btn btn-lg mt-2 form-control-xl input-lg btn-success"
                    > Generate Meme
                    </button>
                    <br />
                </form>
                </div>
                <div className="meme col-md-6 justify-content-center align-items-center">
                <img
                    src={this.state.randomImg}
                    alt="nhi hai madarchod"
                    height="500vh"
                    className="m-5"
                />
                <h2 className="top text-uppercase"> this is the top text</h2>
                <br />
                <h2 className="bottom text-uppercase"> this is the bottom text</h2>
                </div>
            </div>

        )
    }
}


export default MemeGenerator
